import { getBills } from "../../api/index";
// miniprogram/pages/bills/bills.js
const app = getApp()
const icon = 'data:image/png;base64,'
Page({

    /**
     * 页面的初始数据
     */
    data: {
        billList: [],
        isShowGroupTips: false,
        buttons: [
            {
                label: '添加账单',
                icon,
            },
            {
                label: '结算',
                icon
            }
        ]
    },

    query: null,

    /**
     * 生命周期函数--监听页面加载
     */
    onLoad: function (options) {
        this.query = options;
    },

    /**
     * 生命周期函数--监听页面显示
     */
    onShow: function (options) {
        let that = this;
        app.globalData.loginPromise
            .then(res => {
                console.log('bills page after login')
                if (app.globalData.groupInfo) {
                    that.refreshBills();
                } else {
                    console.log('never join group')
                    this.setData({
                        isShowGroupTips: true
                    })
                    if (this.query.joinGroupId) {
                        wx.navigateTo({
                            url: '/pages/join-group/join-group',
                        })
                    }
                }
            })
    },

    onClick(e) {
        let index = e.detail.index;
        switch (index) {
            case 0:
                // 添加账单
                if (app.globalData.groupInfo.isSettlement) {
                    wx.showToast({
                        title: '正在结算中，请完成结算后再添加账单'
                    })
                    return;
                }
                wx.navigateTo({
                    url: '/pages/bill-form/bill-form',
                })
                break;
            case 1:
                // 结算
                wx.navigateTo({
                    url: '/pages/settle-view/settle-view'
                })
                break;
            default:
                break;
        }
    },

    showLoading() {
        wx.showLoading({
            title: '数据加载中...',
            mask: true
        })
    },

    hideLoading() {
        wx.hideLoading();
    },

    refreshBills() {
        let that = this;
        this.showLoading()
        getBills(app.globalData.groupInfo._id)
            .then(res => {
                let billList = res.data.map(item => {
                    item.time = item.time.getTime();
                    item.userInfo = app.globalData.members.find(member => member._openid === item._openid).userInfo
                    item.participants = item.participants.map(item => app.globalData.members.find(member => member._openid === item))
                    return item;
                }).reverse()
                console.log('bill list', billList)
                that.setData({
                    billList: billList
                })
                that.hideLoading();
            })
            .catch(err => {
                console.log('refresh bills failed')
                console.log(err)
            })
    },

    /**
     * 生命周期函数--监听页面隐藏
     */
    onHide: function () {

    },

    /**
     * 生命周期函数--监听页面卸载
     */
    onUnload: function () {

    },

    /**
     * 页面相关事件处理函数--监听用户下拉动作
     */
    onPullDownRefresh: function () {

    },

    /**
     * 页面上拉触底事件的处理函数
     */
    onReachBottom: function () {

    },

    /**
     * 用户点击右上角分享
     */
    onShareAppMessage: function () {

    }
})